"use strict";
const point_of_interest_1 = require("../models/point-of-interest");
var poi = new point_of_interest_1.PointOfInterest("013", "Statue of Jesus", "At the heart of the College.", -26.173185, 28.075152);
poi.themes = Brothers;
today;
at;
Sacred;
